import React from 'react';
import {
    StyleSheet,
    Text,
    View,
    Image,
    ScrollView,
    Platform,
    SafeAreaView,
} from 'react-native';
import {Button} from './Button';

import {launchCamera, launchImageLibrary} from 'react-native-image-picker';

export default function App() {
    const [response, setResponse] = React.useState(null);

    return (
        <SafeAreaView>
            <ScrollView>
                <Button
                    title="Take image"
                    onPress={() =>
                        launchCamera(
                            {
                                mediaType: 'photo',
                                includeBase64: false,
                                maxHeight: 200,
                                maxWidth: 200,
                            },
                            (response) => {
                                setResponse(response);
                            },
                        )
                    }
                />

                <Button
                    title="Select image"
                    onPress={() =>
                        launchImageLibrary(
                            {
                                mediaType: 'photo',
                                includeBase64: false,
                                maxHeight: 200,
                                maxWidth: 200,
                            },
                            (response) => {
                                setResponse(response);
                            },
                        )
                    }
                />

                <Button
                    title="Take video"
                    onPress={() =>
                        launchCamera({mediaType: 'video'}, (response) => {
                            setResponse(response);
                        })
                    }
                />

                <Button
                    title="Select video"
                    onPress={() =>
                        launchImageLibrary({mediaType: 'video'}, (response) => {
                            setResponse(response);
                        })
                    }
                />

                <View style={styles.response}>
                    <Text>Res: {JSON.stringify(response)}</Text>
                </View>

                {response && (
                    <View style={styles.image}>
                        <Image
                            style={{width: 200, height: 200}}
                            source={{uri: response.uri}}
                        />
                    </View>
                )}
            </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: '#F5FCFF',
    },
    button: {
        marginVertical: 24,
        marginHorizontal: 24,
    },
    image: {
        marginVertical: 24,
        alignItems: 'center',
    },
    response: {
        marginVertical: 16,
        marginHorizontal: 8,
    },
});
