import React, { Component } from 'react'
import {
    ActivityIndicator,
    Alert,
    FlatList,
    Text,
    StyleSheet,
    View,
    TextInput,
    TouchableHighlight,
    SafeAreaView,
    ScrollView,
    AppRegistry,
    Button,
    TouchableOpacity, Image,
} from 'react-native';
  import Ionicons from 'react-native-vector-icons/Ionicons';
import axios from "axios";
import {UserContext, Users} from "../Context/context";
import {cart, CartContext} from '../Context/context';

export default class login extends Component {

    constructor() {
        super();
        this.state = {
            email: "",
            password: "",
        };
    }



    submit() {
        axios.post("http://104.236.38.247:8000/api/mobilelogin", {
            email: this.state.email,
            password: this.state.password,
        })
            // .then((res)=>console.log(res.data[0].userID))
            .then((res) =>{ if(res.data[1]=='Login Successfully'){
                this.props.navigation.navigate("MainTabScreen");
                console.log('beforeid#'+ Users.loginuserid);
                Users.loginuserid=res.data[0].userID;
                Users.lguserfname=res.data[0].first_name;
                Users.lguserlname=res.data[0].last_name;
                Users.lguseremail=res.data[0].email;
                console.log('after#'+Users.loginuserid);
                console.log('after#'+Users.lguserfname+Users.lguserlname);


                console.log('beforeid2#'+ cart.loginuserid2);
                cart.loginuserid2=res.data[0].userID;
                console.log('afterid2#'+cart.loginuserid2);
            }else{
                alert("Wrong Login Details");
            }
            })
    }



    out() {
        this.props.navigation.navigate("ReportScreen");
        console.log('beforeid'+ Users.loginuserid);
        Users.loginuserid=null;
        console.log('after'+Users.loginuserid);

    }





    updateValue(text, field) {
        if (field == "email") {
            this.setState({
                email: text,
            });
        }else if (field == "password") {
            this.setState({
                password: text,
            });
        }
    }


    render(){
        return (
            <View style={styles.container}>
                <Image source = {{uri:'data:image/jpeg;base64,'}}
                       style = {{ width: 200, height: 200 }}
                />
                <Text style={styles.logo}>Welcome</Text>
                <View style={styles.inputView} >
                    <TextInput
                        style={styles.inputText}
                        placeholderTextColor="#003f5c"
                        placeholder="Enter email"
                        onChangeText={(text) => this.updateValue(text, "email")}/>
                </View>
                <View style={styles.inputView} >
                    <TextInput
                        style={styles.inputText}
                        placeholderTextColor="#003f5c"
                        secureTextEntry
                        placeholder="Enter password"
                        onChangeText={(text) => this.updateValue(text, "password")}/>
                </View>

                <TouchableOpacity style={styles.loginBtn} title="Submit"
                                  onPress={() => {
                                      this.submit();
                                  }}>
                    <Text style={styles.loginText}>LOGIN</Text>
                </TouchableOpacity>



            </View>
        );
    }
}
login.contextType = UserContext,CartContext;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#003f5c',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo:{
        fontWeight:"bold",
        fontSize:50,
        color:"#fb5b5a",
        marginBottom:40,
        alignItems:"center",
        justifyContent:"center",
        textAlign:"center"
    },
    inputView:{
        width:"80%",
        backgroundColor:"#465881",
        borderRadius:25,
        height:50,
        marginBottom:20,
        justifyContent:"center",
        padding:20
    },
    inputText:{
        height:50,
        color:"white"
    },
    forgot:{
        color:"white",
        fontSize:11
    },
    loginBtn:{
        width:"50%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginBottom:10
    },
    loginText:{
        color:"white"
    }
});
