        return (
            <ScrollView style={styles.scrollView}>
                <View style={styles.container}>
                    {/* <Text  style={{
                  color: 'green',
                  textAlign: 'center',
                  fontWeight: 'bold',
                  fontSize: 35,
                }}>MY ROUTES</Text> */}
                    {this.state.arr.map((item, i) => {
                        return (
                            <View key={i} style={styles.container1}>
                                <Text
                                    style={{
                                        color: 'blue',
                                        textAlign: 'center',
                                        fontWeight: 'bold',
                                        fontSize: 25,
                                    }}>
                                    {item.route_name}{' '}
                                </Text>
                                <Text> </Text>
                                <Button
                                    key={i}
                                    onPress={() =>
                                        this.setState((cur) => ({
                                            ...cur,
                                            data: {
                                                source: {
                                                    latitude: null,
