import React, { useState, useEffect } from "react";
import {
    StyleSheet,
    View,
    FlatList,
    ActivityIndicator,
    Text,
    SafeAreaView,
} from "react-native";
import axios from "axios";

class InquiryFlatList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            dataSource: null,
        };
    }

    componentDidMount() {
        return fetch("/user/inquiry")
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    isLoading: false,
                    dataSource: responseJson,
                });
            })

            .catch((error) => {
                console.log(error);
            });
    }

    render() {
        if (this.state.isLoading) {
            return (
                <View>
                    <ActivityIndicator />
                </View>
            );
        } else {
            let inquiry = this.state.dataSource.map((val, key) => {
                return (
                    <View key={key}>
                        <Text> {val.userid} </Text>
                    </View>
                );
            });

            return <View style={styles.container}>{inquiry}</View>;
        }
    }
}

const styles = StyleSheet.create({
    input: {
        borderWidth: 2,
        borderColor: "skyblue",
        margin: 20,
        marginTop: 20,
    },
    container: {
        margin: 20,
        marginTop: 100,
    },
});

export default InquiryFlatList;
