            </TouchableOpacity>
          </View>
        </View>

        <Text style={styles.headerText}>Product List</Text>

        {/*Starting search bar ///////////////////////////////////*/}
        <View
          style={{
            marginTop: 10,
            marginLeft: 10,
            marginRight: 10,
            marginBottom: 20,
            flexDirection: 'row',
            /*justifyContent:'space-between',
