              borderRadius: 10,
              flexDirection: 'row',
              backgroundColor: '#add',
              alignItems: 'center',
              paddingHorizontal: 20,
            }}>
            <Ionicons name="search" size={28} />
            <TextInput
              onChangeText={(text) => this.searchData(text)}
              value={this.state.text}
              underlineColorAndroid="transparent"
              placeholder="Search Here (Type product name)"
            />
          </View>
        </View>

        {/*Ending search bar ///////////////////////////////////*/}

        <FlatList
          data={this.state.data}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({item, index}) => (
            <View style={styles.card}>
              <View style={styles.cardInfo}>
                <Text style={styles.cardTitle}>
                  Product Name : {item.product_Name}
                </Text>

                <Text style={styles.cardTitle}>
                  Sales Price : {item.sales_price}
                </Text>

                <Text style={styles.cardTitle}>
                  Labeled Price : {item.labled_price}
                </Text>

                <Text style={styles.cardTitle}>Weight: {item.weight}</Text>

                <View style={styles.formRow}>
                  <TextInput
                    style={styles.textInput}
                    keyboardType="numeric"
                    placeholder="Enter Quantity"
                    placeholderTextColor="#333"
                    value={item.qty ? item.qty : 0}
                    onChangeText={(text) => this.qtyChangeFunction(index, text)}
                  />

                  <View style={styles.buttonContainer}>
                    <Button
                      onPress={() =>
                        this.addButtonOnClickHandler(
                          item.product_Name,
                          item.qty,
                          item.sales_price,
                          item.productID,
                        )
                      }
                      title="ADD to cart"
                    />
                  </View>
                </View>
              </View>
            </View>
          )}
        />





        <View style={styles.productCartOverallViewB}>
          <View style={styles.productCartInlineViewB}>
            <TouchableOpacity
                onPress={() => this.props.navigation.navigate('Home')}>
              <Ionicons name="ios-home" size={26} color="#000000" />
            </TouchableOpacity>
          </View>
          <View style={styles.productCartInlineViewB}>
            <TouchableOpacity
                onPress={() =>
                    this.props.navigation.navigate('Category', { title: 'Cart' })
                }>
              <Ionicons name="ios-apps" size={26} color="#000000" />
            </TouchableOpacity>
          </View>
          <View style={styles.productCartInlineViewB}>
            <TouchableOpacity
                onPress={() =>
                    this.props.navigation.navigate('product', { title: 'Product' })
                }>
              <Ionicons name="pricetags" size={26} color="#FFFFFF" />
            </TouchableOpacity>
          </View>

          <View style={styles.productCartInlineViewB}>
            <TouchableOpacity
                onPress={() =>
                    this.props.navigation.navigate('Cart', { title: 'Cart' })
                }>
              <Ionicons name="cart" size={26} color="#000000" />
            </TouchableOpacity>
          </View>

        </View>







      </View>
    );
  }
}

