                //onChangeText={(val) => setCompanyAddress(val)}
            />


            <TextInput
                label="Contact Number"
                value={contact_no}
                //onChangeText={(val) => setCompanyAddress(val)}
            />

            <TextInput
                label="EMAIL"
                value={email}
                //onChangeText={(val) => setCompanyAddress(val)}
