import React, { useState } from "react";
import {
  StyleSheet,
  View,
  TextInput,
  Button,
  RadioButtonGroup,
  RadioButton,
  SafeAreaView, ScrollView,
  Text,TouchableOpacity
} from "react-native";
import {Users,UserContext} from '../Context/context';
import axios from "axios";
import ExploreScreen from "./ExploreScreen";
import Ionicons from 'react-native-vector-icons/Ionicons';
import Icon from 'react-native-vector-icons/Ionicons';
import MaterialIconsIcon from "react-native-vector-icons/MaterialIcons";


const initialState = {
  unproductive_reason: '',
}

class unproductive extends React.Component {
  constructor() {
    super();
    this.state = initialState;
  }



  submit() {
    axios.post('http://104.236.38.247:8000/api/shopclosereason/'+this.context.unpshopid, {
      unproductive_reason: this.state.unproductive_reason,
    })
        .then((res) => {
          console.log(res.data)
          this.props.navigation.navigate('Explore')
          console.log('beforeunoid#'+Users.unpshopid);
          (Users.unpshopid=null);
          console.log('afterunoid#'+Users.unpshopid);
          this.setState(initialState);

        })
        .catch((error) => console.log(error));
  }

  updateValue(text, field) {
    if (field == "unproductive_reason") {
      this.setState({
        unproductive_reason: text,
      });
    }
  }

  render() {
    const { navigation } = this.props;

    return (
        <SafeAreaView style={styles.container}>
          <View style={styles.rect}>
            <View style={styles.iconRow}>
              <Icon name="md-menu" style={styles.icon} onPress={() => navigation.openDrawer()}></Icon>
              <Text style={styles.cart}>Unproductive</Text>
            </View>
          </View>

          <ScrollView style={styles.scrollView}>
            <View>
              <Text
                  style={{
                    color: '#FF0000',
                    marginLeft :20,
                    fontWeight: 'bold',
                    fontSize: 20,
                    marginTop :10
                  }}>
                Add unproductive reason
              </Text>

            </View>


            <View style={styles.container}>
              <TextInput
                  style={styles.input}
                  placeholder="Enter reason"
                  value={this.state.unproductive_reason}
                  onChangeText={(text) => this.updateValue(text, "unproductive_reason")}
              />

              {/*<Button*/}
              {/*    title="Submit"*/}
              {/*    onPress={() => {*/}
              {/*      this.submit();*/}

              {/*    }}*/}
              {/*/>*/}
              <TouchableOpacity
                  onPress={() => {
                    this.submit();
                  }}
                  style={{
                    height: 42,
                    width: 100,
                    justifyContent: 'center',
                    alignItems: 'center',
                    alignSelf: 'center',
                    backgroundColor: '#2B547E',
                    borderRadius: 20,
                  }}>
                <Text
                    style={{
                      color: 'white',
                      textAlign: 'center',
                      fontWeight: 'bold',
                      fontSize: 16,
                    }}>
                  Submit
                </Text>
              </TouchableOpacity>

            </View>
          </ScrollView>


            <View style={styles.productCartOverallViewB}>
              <View style={styles.productCartInlineViewB}>
                <TouchableOpacity
                    onPress={() => this.props.navigation.navigate('Home')}>
                  <Ionicons name="ios-home" size={26} color="#000000" />
                </TouchableOpacity>
              </View>
              <View style={styles.productCartInlineViewB}>
                <TouchableOpacity
                    onPress={() =>
                        this.props.navigation.navigate('Explore', { title: 'Explore' })
                    }>
                  <Ionicons name="ios-star" size={26} color="#000000" />
                </TouchableOpacity>
              </View>



              <View style={styles.productCartOverallViewB}>
                <View style={styles.productCartInlineViewA}>
                  <TouchableOpacity
                      onPress={() => this.props.navigation.navigate('Home')}>
                    <Ionicons name="ios-home" size={26} color="#FFFFFF" />
                  </TouchableOpacity>
                </View>
                <View style={styles.productCartInlineViewB}>
                  <TouchableOpacity
                      onPress={() =>
                          this.props.navigation.navigate('Explore', { title: 'Explore' })
                      }>
                    <MaterialIconsIcon name="store" size={30} color="#FFFFFF"></MaterialIconsIcon>
                  </TouchableOpacity>
                </View>


</View>
              </View>
        </SafeAreaView>
    );
  }
}
unproductive.contextType = UserContext;
  const styles = StyleSheet.create({
      container: {
        flex: 1,


      },

      scrollView: {

        marginHorizontal: 1,
      },


      input: {
        backgroundColor: '#add',
            height: 40,
            paddingHorizontal: 10,
            color: '#808080',
            borderRadius: 20,
      },
      empty: {
        textAlign: 'center',
      },

      addProductBtn: {
        backgroundColor: 'blue',
            paddingVertical: 10,
      },

      addProductText: {
        textAlign: 'center',
            color: '#fff',
            fontSize: 15,
            fontWeight: 'bold',
      },

      // container:{
      //     height:"100%",
      //     alignItems:"center",
      //     justifyContent:"center"
      // }

      formWrapper: {
        width: '80%',
            justifyContent: 'center',
      },

      formRow: {
        marginBottom: 10,
      },

      textInput: {
        backgroundColor: '#add',
            height: 40,
            paddingHorizontal: 10,
            color: '#333',
            borderRadius: 20,
      },

      welcomeText: {
        textAlign: 'center',
            marginBottom: 20,
            fontSize: 24,
            fontWeight: 'bold',
            color: '#2B547E',
      },
      regText: {
        textAlign: 'center',
            marginBottom: 10,
            fontSize: 14,
            fontWeight: 'bold',

      },
      rect: {
        width: 415,
            height: 62,
            backgroundColor: "rgba(11,1,47,1)",
            flexDirection: "row",
            marginTop: -6
      },
      icon: {
        color: "rgba(255,255,255,1)",
            fontSize: 30,
            height: 44,
            width: 40,
      },
      cart: {
        fontWeight: 'bold',
            color: "rgba(245,245,245,1)",
            fontSize: 19,
            marginLeft: 1,
            marginTop: 4
      },
      iconRow: {
        height: 44,
            flexDirection: "row",
            flex: 1,
            marginRight: 280,
            marginLeft: 8,
            marginTop: 13
      },
      productCartInlineViewB: {

        alignItems: 'center',
            justifyContent: 'center',
            alignSelf: 'center',
            width: 100,
            height: 25,

            // borderRadius: 60,
            textAlign: 'center',
            marginRight: -60,
             marginLeft:0,
            marginTop: 10,
            marginBottom: 10,
      },
    productCartInlineViewA: {

      alignItems: 'center',
      justifyContent: 'center',
      alignSelf: 'center',
      width: 100,
      height: 25,

      // borderRadius: 60,
      textAlign: 'center',
      marginRight: 80,
      marginLeft:-10,
      marginTop: 10,
      marginBottom: 10,
    },
      productCartOverallViewB: {
        flexDirection: 'row',
            width: '100%',
            alignSelf: 'center',
            marginTop: 10,


            backgroundColor: 'rgba(11,1,47,1)'
      },

    });

export default unproductive;











