import React, {Component} from 'react';
import {
    StyleSheet,
    View,
    Text,
    TouchableOpacity,
    TextInput,
    ScrollView,
    Button,
    SafeAreaView,
} from 'react-native';
import {Table, TableWrapper, Row, Cell,Rows} from 'react-native-table-component';
import {Users,UserContext} from '../Context/context';
import Ionicons from 'react-native-vector-icons/Ionicons';
import axios from "axios";

const initialState = {
    tableHead: [
        'Item No',
        'Shop Name',
        'No Of Bills',
        'Total bill Value',
    ],
    tableData: [],
    tableFooter:['','','',''],
    TotalBills:0,
    TotalBillValue:0,

};


export default class BillsFromShops extends Component {
    constructor() {
        super();
        this.state = initialState;
    }



    componentDidMount() {
        axios.get('http://104.236.38.247:8000/api/billsfromshops/'+this.context.loginuserid,{
            params:{
                start_date:this.context.startdate,
                end_date:this.context.enddate,
            }


        })
            .then((res) => {
                console.log(res.data);
                let count = Object.keys(res.data).length;
                console.log(count);
                (this.state.TotalBills=0);
                (this.state.TotalBillValue=0);
                for (let i = 0; i < count; i++) {
                    this.setState((cur) => ({
                        tableData: [...cur.tableData,[i+1,res.data[i].shop_name,res.data[i].numOfBills,parseFloat(res.data[i].totalValue).toFixed(2)]],
                        ...cur.TotalBills+=res.data[i].numOfBills,
                        ...cur.TotalBillValue+=res.data[i].totalValue,

                    }));
                }
                this.setState((cur)=>({
                    tableFooter:[null,'Total',this.state.TotalBills,parseFloat(this.state.TotalBillValue).toFixed(2)]
                }));
                console.log(this.state.tableData);
                console.log(this.state.TotalBills);
                console.log('Total bill value ->>>'+this.state.TotalBillValue);
            })
            .catch((error) => {
                console.log(error.message);
            });
    }



    render() {
        return (
            <View>
                <Text>
                    item details
                </Text>

                <Table borderStyle={{borderColor: 'transparent'}} borderStyle={{borderWidth: 5, borderColor: '#c8e1ff'}}>
                    <Row
                        data={this.state.tableHead}
                        style={styles.head}
                        textStyle={styles.text}
                    />
                </Table>
                <Table borderStyle={{borderColor: 'transparent'}}>

                    <Rows data={this.state.tableData} style={styles.row} textStyle={styles.text}/>
                </Table>



                <Table borderStyle={{borderColor: 'transparent'}} borderStyle={{borderWidth: 5, borderColor: '#c8e1ff'}}>
                    <Row
                        data={this.state.tableFooter}
                        style={styles.head}
                        textStyle={styles.text}
                    />
                </Table>



                <TouchableOpacity style={styles.loginBtn} title="Submit"
                                  onPress={() => {
                                      this.setState(initialState);
                                      this.context.startdate="",
                                      this.context.enddate="",
                                      console.log('context end date'+this.context.enddate);
                                      this.props.navigation.navigate('Report');

                                  }}>
                    <Text style={styles.loginText}>Main Menu</Text>
                </TouchableOpacity>




            </View>

        );
    }
}


BillsFromShops.contextType = UserContext;



const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#003f5c',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo:{
        fontWeight:"bold",
        fontSize:50,
        color:"#fb5b5a",
        marginBottom:40,
        alignItems:"center",
        justifyContent:"center",
        textAlign:"center"
    },
    inputView:{
        width:"80%",
        backgroundColor:"#465881",
        borderRadius:25,
        height:50,
        marginBottom:20,
        justifyContent:"center",
        padding:20
    },
    inputText:{
        height:50,
        color:"white"
    },
    forgot:{
        color:"white",
        fontSize:11
    },
    loginBtn:{
        width:"50%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginBottom:10
    },
    loginText:{
        color:"white"
    },
    head: { height: 40, backgroundColor: '#808B97' },
    row: { flexDirection: 'row', backgroundColor: '#FFF1C1' },
    text: { textAlign: 'center', fontWeight: '100',margin:6 },
});
