import React, { Component } from 'react';
import {
    ActivityIndicator,
    Alert,
    FlatList,
    Text,
    StyleSheet,
    View,
    TextInput,
    TouchableHighlight,
    Button,
    SafeAreaView,
    ScrollView,
    TouchableOpacity
} from 'react-native';
import axios from "axios";
import {Users,UserContext} from '../Context/context';
import {cart, CartContext} from '../Context/context';


export default class ExploreScreen extends Component {

    state = {
        arr: [],
        
    };





    componentDidMount() {
        axios
            .get('http://104.236.38.247:8000/api/viewshops/'+this.context.loginuserid)
            .then((res) => {

                console.log('S->length is'+res.data.length);
                console.log('S->userid is'+this.context.loginuserid);
                let count = Object.keys(res.data).length;
                for (let i = 0; i < count; i++) {
                    this.setState((cur) => ({
                        arr: [...cur.arr, res.data[i]],
                    }));
                }
            })
            .catch((error) => {
                console.log(error.message);
            });
    }

    funct(id){
        (cart.shopidfo=id);
        console.log(cart.shopidfo);
        {this.props.navigation.navigate('Notifications')};
    }


    unproductive(id){
        (Users.unpshopid=id);
        console.log(Users.unpshopid);
        {this.props.navigation.navigate('unproductive')};
    }

    more(id){
        console.log('before'+Users.moreshopid);
        (Users.moreshopid=id);
        console.log('after'+Users.moreshopid);
        {this.props.navigation.navigate('moreshop')};
    }










    render() {
        return (
            <ScrollView style={styles.scrollView}>
                <View style={styles.container}>

                    <TouchableOpacity  style={styles.AddBtn}
                                       title="Add Shop"
                                      onPress={() => {
                                          this.props.navigation.navigate('ReturnDetails')
                                      }}>
                        <Text >Add Shop</Text>
                    </TouchableOpacity>


                    <Text  style={{
                  color: 'green',
                  textAlign: 'center',
                  fontWeight: 'bold',
                  fontSize: 35,
                }}>MY shops</Text>
                    {this.state.arr.map((item, i) => {
                        return (
                            <View key={i} style={styles.container1}>
                                <Text
                                    style={{
                                        color: 'blue',
                                        textAlign: 'left',
                                        fontWeight: 'bold',
                                        fontSize: 25,
                                    }}>
                                    {item.shop_name}{' '}
                                </Text>



                                <Button

                                    onPress={() =>
                                    this.unproductive(item.ShopID)}
                                    title="UNP"
                                />

                                <Button

                                    onPress={() =>
                                        this.funct(item.ShopID)}
                                    title="Order"
                                />

                                <Button

                                    onPress={() =>
                                        this.more(item.ShopID)}
                                    title="More"
                                />

                            </View>
                        );
                    })}
                </View>

            </ScrollView>

        );
    }

}
ExploreScreen.contextType = UserContext;
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#F5FCFF',
    },
    container1: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        // backgroundColor: '#000000',
    },
    AddBtn:{
        width:"50%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginBottom:10
    },
    map: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
    },
});
