import React, {Component} from 'react';

import {
  ActivityIndicator,
  Alert,
  FlatList,
  Text,
  StyleSheet,
  View,
  TextInput,
  TouchableHighlight,
  SafeAreaView,
  ScrollView,
  TouchableOpacity, Button,
} from 'react-native';
import {cart, CartContext} from '../Context/context';
import Ionicons from 'react-native-vector-icons/Ionicons';
import product from "./product";
import Icon from 'react-native-vector-icons/Ionicons';

class Category extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      text: '',
      data: [],
    };

    this.arrayholder = [];
  }

  componentDidMount() {
    return fetch('http://104.236.38.247:8000/api/viewcategories/')
        .then((response) => response.json())
        .then((responseJson) => {
          // console.log(responseJson);
          this.setState(
              {
                isLoading: false,
                data: responseJson,
              },
              () => {
                // In this block you can do something with new state.
                this.arrayholder = responseJson;
              },
          );
        })
        .catch((error) => {
          console.error(error);
        });
  }

  GetFlatListItem(name) {
    Alert.alert(name);
  }

  searchData(text) {
    const newData = this.arrayholder.filter((item) => {
      const itemData = item.category_name.toUpperCase();
      const textData = text.toUpperCase();
      return itemData.indexOf(textData) > -1;
    });

    this.setState({
      data: newData,
      text: text,
    });
  }

  itemSeparator = () => {
    return (
        <View
            style={{
              height: 0.5,
              width: '100%',
              backgroundColor: '#000',
            }}
        />
    );
  };

  render() {
    const { navigation } = this.props;
    if (this.state.isLoading) {
      return (
          <View style={{ flex: 1, paddingTop: 20 }}>
            <ActivityIndicator />
          </View>
      );
    }

    return (
        <View style={styles.MainContainer}>
          <View style={styles.container}>
            <StatusBar animated backgroundColor="rgba(4,4,13,1)" />
            <View style={styles.rect}>
              <View style={styles.iconRow}>
                <Icon name="md-menu" style={styles.icon} onPress={() => navigation.openDrawer()}></Icon>
                <Text style={styles.cart}>Category</Text>
              </View>
            </View>
          </View>
          <View style={styles.productCartOverallView}>
            <View style={styles.productCartInlineView}>
              <TouchableOpacity
                  onPress={() => this.props.navigation.navigate('Category')}>
                <Ionicons name="cog" size={30} color="#009387" />
              </TouchableOpacity>
            </View>

            <View style={styles.productCartInlineView}>
              <TouchableOpacity
                  onPress={() =>
                      this.props.navigation.navigate('product', { title: 'Product' })
                  }>
                <Ionicons name="pricetags" size={30} color="#009387" />
              </TouchableOpacity>
            </View>

            <View style={styles.productCartInlineView}>
              <TouchableOpacity
                  onPress={() =>
                      this.props.navigation.navigate('Cart', { title: 'Cart' })
                  }>
                <Ionicons name="cart" size={30} color="#009387" />
              </TouchableOpacity>
            </View>
          </View>
          {/*Ending*/}
          <Text style={styles.headerText}>Category List</Text>
          {/*Starting Search bar /////////////*/}
          <View
              style={{
                marginTop: 10,
                marginLeft: 10,
                marginRight: 10,
                marginBottom: 20,
                flexDirection: 'row',
                justifyContent: 'space-between',
                paddingHorizontal: 20,
              }}>
            <View
                style={{
                  flex: 1,
                  height: 50,
                  borderRadius: 10,
                  flexDirection: 'row',
                  backgroundColor: '#add',
                  alignItems: 'center',
                  paddingHorizontal: 20,
                }}>
              <Ionicons name="search" size={28} />
              <TextInput
                  onChangeText={(text) => this.searchData(text)}
                  value={this.state.text}
                  underlineColorAndroid="transparent"
                  placeholder="Search Here (Type category name)"
              />
            </View>
          </View>
          {/*Starting Search bar /////////////*/}

          <FlatList
              data={this.state.data}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({ item }) => (
                  <View style={styles.card}>
                    <View style={styles.cardInfo}>
                      <TouchableOpacity
                          onPress={() => {
                            this.props.navigation.navigate('product', {
                              title: 'Product',
                            });
                            console.log('beforecatid'+cart.categoryid);
                            (cart.categoryid=item.categoryID);
                            console.log('aftercatid'+cart.categoryid);
                            console.log('shopId' + cart.shopidfo);
                          } }>
                        <Text style={styles.cardTitle}>
                          Category Name : {item.category_name}
                        </Text>
                      </TouchableOpacity>
                    </View>
                  </View>
              )}
          />
          <View style={styles.productCartOverallViewB}>
            <View style={styles.productCartInlineViewB}>
              <TouchableOpacity
