import React, { useState } from "react";
import {
    StyleSheet,
    View,
    TextInput,
    Button,
    RadioButtonGroup,
    RadioButton,
    SafeAreaView, ScrollView,
    Text
} from "react-native";

import {launchCamera, launchImageLibrary} from 'react-native-image-picker';

import axios from "axios";

class Inquiry extends React.Component {
    constructor() {
        super();
        this.state = {
            shop_name: "",
            owner_name: "",
            owner_NIC: "",
            address_no: "",
            suburb: "",
            city: "",
            province: "",
            country: "",
            telephone_numbers: "",
            user_id: "",
        };
    }

    submit() {
        axios.post("http://104.236.38.247:8000/api/mobileshopadd", {
            shop_name: this.state.shop_name,
            owner_name: this.state.owner_name,
            owner_NIC: this.state.owner_NIC,
            address_no: this.state.address_no,
            suburb: this.state.suburb,
            city: this.state.city,
            province: this.state.province,
            country: this.state.country,
            telephone_numbers: this.state.telephone_numbers,
            user_id: this.state.user_id,
        })
            .then((res) => console.log(res))
            .catch((error) => console.log(error));
    }

    updateValue(text, field) {
        if (field == "shop_name") {
            this.setState({
                shop_name: text,
            });
        } else if (field == "owner_name") {
            this.setState({
                owner_name: text,
            });
        } else if (field == "owner_NIC") {
            this.setState({
                owner_NIC: text,
            });
        } else if (field == "address_no") {
            this.setState({
                address_no: text,
            });
        } else if (field == "suburb") {
            this.setState({
                suburb: text,
            });
        } else if (field == "city") {
            this.setState({
                city: text,
            });
        } else if (field == "province") {
            this.setState({
                province: text,
            });
        } else if (field == "country") {
            this.setState({
                country: text,
            });
        } else if (field == "telephone_numbers") {
            this.setState({
                telephone_numbers: text,
            });
        } else if (field == "user_id") {
            this.setState({
                user_id: text,
            });
        }
    }






    render() {
        return (
            <SafeAreaView style={styles.container}>
                <ScrollView style={styles.scrollView}>
                    <View style={styles.container}>
                        <TextInput
                            style={styles.input}
                            placeholder="Enter Shop Name"
                            onChangeText={(text) => this.updateValue(text, "shop_name")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter Owner Name"
                            onChangeText={(text) => this.updateValue(text, "owner_name")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter NIC"
                            onChangeText={(text) => this.updateValue(text, "owner_NIC")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter Address No"
                            onChangeText={(text) => this.updateValue(text, "address_no")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter Suburb"
                            onChangeText={(text) => this.updateValue(text, "suburb")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter City"
                            onChangeText={(text) => this.updateValue(text, "city")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter Province"
                            onChangeText={(text) => this.updateValue(text, "province")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter Country"
                            onChangeText={(text) => this.updateValue(text, "country")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter TP"
                            onChangeText={(text) => this.updateValue(text, "telephone_numbers")}
                        />

                        <TextInput
                            style={styles.input}
                            placeholder="Enter User ID"
                            onChangeText={(text) => this.updateValue(text, "user_id")}
                        />


                        <Button title="Upload Photo"  />

                        <Button
                            title="Submit"
                            onPress={() => {
                                this.submit();
                            }}
                        />
                    </View>
                </ScrollView>
            </SafeAreaView>
        );
    }
}

const styles = StyleSheet.create({
    input: {
        borderWidth: 2,
        borderColor: "skyblue",
        margin: 20,
        marginTop: 20,
    },
    container: {
        margin: 20,
        marginTop: 100,
    },
    scrollView: {
        backgroundColor: 'pink',
        marginHorizontal: 20,
    },
    text: {
        fontSize: 42,
    },
});

export default Inquiry;
