import React, {Component} from 'react';
import {
    StyleSheet,
    View,
    Text,
    TouchableOpacity,
    TextInput,
    ScrollView,
    Button,
    SafeAreaView
} from 'react-native';
import {Table, TableWrapper, Row, Cell,Rows} from 'react-native-table-component';
import {cart, CartContext} from '../Context/context';
import {Users,UserContext} from '../Context/context';
import Ionicons from 'react-native-vector-icons/Ionicons';
import ProfileScreen from "./ProfileScreen";
import axios from "axios";
import Icon from 'react-native-vector-icons/Ionicons';
const initialState = {
    tableHead: [
        'Item No',
        'Product Name',
        'Quantity',
    ],
    tableFooter:[0,''],
    tableData: ['','',''],
    BillValue:null,
    placedDate:'',
    shopName:'',
    firstName:'',
    lastName:'',

};


export default class OrderDescription extends Component {
    constructor() {
        super();
        this.state = initialState;
    }



    componentDidMount() {
        axios.get('http://104.236.38.247:8000/api/orderdetails/'+this.context.oderDescription)
            .then((res) => {
                // console.log('---------------descripting---------------------------------------------');
                // console.log(res.data.description);
                // console.log('---------------------data---------------------------------------------');
                // console.log(res.data.data);
                // console.log(this.context.oderDescription);
                let count = Object.keys(res.data.data).length;
                // console.log(count);
                // (this.state.TotalQuantity=0);
                // (this.state.TotalBillValue=0);
                for (let i = 0; i < count; i++) {
                    this.setState((cur) => ({
                        tableData: [...cur.tableData,[i+1,res.data.data[i].product_Name,res.data.data[i].quantity_per_product]],
                    }));

                }


                this.setState({
                    BillValue:(res.data.description[0].bill_value),
                    placedDate:(res.data.description[0].placed_date),
                    shopName:(res.data.description[0].shop_name),
                    firstName:(res.data.description[0].first_name),
                    lastName:(res.data.description[0].last_name)
                });


            })
            .catch((error) => {
                console.log(error.message);
            });
    }





    render() {
        const { navigation } = this.props;
        return (
            <SafeAreaView style={styles.container}>
                <View style={styles.rect}>
                    <View style={styles.iconRow}>
                        <Icon name="md-menu" style={styles.icon} onPress={() => navigation.openDrawer()}></Icon>
                        <Text style={styles.cart}>Order Details</Text>
                    </View>
                </View>

                <ScrollView style={styles.scrollView}>

                 <View style={{
                     height:150,
                     width: '90%',
                     justifyContent: 'center',
                     alignItems: 'center',
                     backgroundColor: '#add8e6',
                     marginVertical: 10,
                     marginLeft:18,
                     borderRadius:15,
                 }}>
                     <Text style={styles.txt}>Shop Name:{'  '}{this.state.shopName}</Text>
                     <Text style={styles.txt}>Place by :{'   '}{this.state.firstName} {this.state.lastName}</Text>
                    <Text style={styles.txt}>Placed Date :{'  '}{this.state.placedDate}</Text>
                     <Text style={styles.txt}>Total Bill Value is :{'   '}{this.state.BillValue}</Text>

                 </View>

                    <Table borderStyle={{borderColor: 'transparent'}} borderStyle={{borderWidth: 5, borderColor: '#c8e1ff'}}>
                        <Row
                            data={this.state.tableHead}
                            style={styles.head}
                            textStyle={styles.text}
                        />
                    </Table>
                    <Table borderStyle={{borderColor: 'transparent'}}>

                        <Rows data={this.state.tableData} style={styles.row} textStyle={styles.text}/>
                    </Table>




                </ScrollView>
                <View style={styles.productCartOverallViewB}>
                    <View style={styles.productCartInlineViewB}>
                        <TouchableOpacity
                            onPress={() => this.props.navigation.navigate('Home')}>
                            <Ionicons name="ios-home" size={26} color="#000000" />
                        </TouchableOpacity>
                    </View>
                    <View style={styles.productCartInlineViewB}>
                        <TouchableOpacity
                            onPress={() =>
                                this.props.navigation.navigate('Report', { title: 'Explore' })
                            }>
                            <Ionicons name="ios-create" size={26} color="#000000" />
                        </TouchableOpacity>
                    </View>
                    <View style={styles.productCartInlineViewB}>
                        <TouchableOpacity
                            onPress={() =>
                                this.props.navigation.navigate('SelectDate')
                            }>
                            <Ionicons name="md-calculator" size={26} color="#000000" />
                        </TouchableOpacity>
                    </View>


                </View>


            </SafeAreaView>

        );
    }
}


OrderDescription.contextType = UserContext;



const styles = StyleSheet.create({
    container: {
        flex: 1,

    },
    logo:{
        fontWeight:"bold",
        fontSize:50,
        color:"#fb5b5a",
        marginBottom:40,
        alignItems:"center",
        justifyContent:"center",
        textAlign:"center"
    },
    inputView:{
        width:"80%",
        backgroundColor:"#465881",
        borderRadius:25,
        height:50,
        marginBottom:20,
        justifyContent:"center",
        padding:20
    },
    inputText:{
        height:50,
        color:"white"
    },
    forgot:{
        color:"white",
        fontSize:11
    },
    loginBtn:{
        width:"50%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginBottom:10
    },
    loginText:{
        color:"white"
    },
    head: { height: 40, backgroundColor: '#808B97' },
    row: { flexDirection: 'row', backgroundColor: '#FFF1C1' },
    text: { textAlign: 'center', fontWeight: '100',margin:6 },
    rect: {
        width: 415,
        height: 62,
        backgroundColor: "rgba(11,1,47,1)",
        flexDirection: "row",
        marginTop: -6
    },
    icon: {
        color: "rgba(255,255,255,1)",
        fontSize: 30,
        height: 44,
        width: 40
    },
    cart: {
        fontWeight:'bold',
        color: "rgba(245,245,245,1)",
        fontSize: 18,
        // marginLeft: 20,
        marginTop: 2
    },
    iconRow: {
        height: 44,
        flexDirection: "row",
        flex: 1,
        marginRight: 280,
        marginLeft: 8,
        marginTop: 13
    },
    productCartInlineViewB: {

        alignItems: 'center',
        justifyContent: 'center',
        alignSelf: 'center',
        width: 100,
        height: 25,

        // borderRadius: 60,
        textAlign: 'center',
        marginRight: 20,
        marginLeft: 20,
        marginTop: 15,
        marginBottom: 10,
    },
    productCartOverallViewB: {
        flexDirection: 'row',
        width: '100%',
        alignSelf: 'center',
        marginTop: 299,
        marginBottom:-10,
        backgroundColor: '#2E5E90'
    },
    txt:{
    color: 'blue',
    textAlign: 'justify',
    fontWeight: 'bold',
    fontSize: 17,
}
});
